<?php

include_once( 'parser.php' );

class TParser_ellos extends TParser_catalog
{
    protected function foreach_item( $dom, $cat )
    {
        var_dump('COUNT = '.$this->i);
        $table = $dom->getElementById( 'ctl00_cphMain_ctl00_dlProducts' );
        $tds = $table->getElementsByTagName( 'td' );
        foreach ( $tds as $td )
        {
            if ( ($attr = $td->attributes->getNamedItem( 'class' )) !== null )
            {
                if ( $attr->value == 'showProdV-tdProduct' || $attr->value == 'showProdV-tdProductNoPadRight' )
                {
                    $this->item( $td, $cat );
                }
            }
        }
        
        //var_dump('__EVENTTARGET=ctl00$cphMain$ctl00$lnkViewAllTop&__EVENTARGUMENT=&__LASTFOCUS=&__VIEWSTATE='.urlencode('').'&ctl00$ctl04$txtNewsLetter=&ctl00$ctl04$chkNewsLetter=on&ctl00$ctl05$txtSearch:=Найти товар(ы)&ctl00$cphMain$ctl00$hidPriceFilter=0&ctl00$cphMain$ctl00$hidAlphaFilter=0&ctl00$cphMain$ctl00$hidViewAll=0&ctl00$cphMain$ctl00$hidStartIndex=1&ctl00$cphMain$ctl00$hidTotalNumberOfPages=3&ctl00$cphMain$ctl00$cmbFilterColour=-1&ctl00$cphMain$ctl00$cmbFilterBrands=-1&ctl00$cphMain$ctl00$cmbFilterSize=:-1' );
        var_dump('COUNT = '.$this->i);
    }

    protected function url( $node, $url='' )
    {
        $diva = $this->getElement( $node, 'div', 'showProdV-divProdImage' );
        $a = $this->getElement( $diva, 'a' );
        return $url.preg_replace( '/^(\/)/', '', $a->attributes->getNamedItem( 'href' )->value );
    }

    protected function picture( $node, $url='' )
    {
        $diva = $this->getElement( $node, 'div', 'showProdV-divProdImage' );
        $img = $this->getElement( $diva, 'img' );

        return $img->attributes->getNamedItem( 'src' )->value;
    }

    protected function name( $node )
    {
        $div = $this->getElement( $node, 'div', 'showProdV-divTitlePrice' );
        $td = $this->getElement( $node, 'td', 'showProdV-tdTagRight' );
        return $this->getElementValue( $div, 'a').''.$td->nodeValue;
    }

    protected function price( $node )
    {
        return mb_substr(preg_replace( '([^0-9])', '', $this->getElementValue( $node, 'span', 'showProdV-spanPrice' )),0,-2 );
    }

    protected function price_old( $node )
    {
        return null;
    }

    protected function sale( $node )
    {
        if($this->getElement($node,"div","showProdV-divTag9")){
            return "-20";
        }else{
            return null;
        }
    }

    protected function size( $node )
    {
        $td = $this->getElement( $node, 'td', 'singleProdV-tdProdLeft' );
        $js = $this->getElement( $td, 'script' );

        $expjs = explode( ';', $js->nodeValue );
        foreach( $expjs as $j )
        {
            preg_match( "/new CProduct\(([^\.\,\s]+)\,[\s]+?([^\.\,\s]+),[\s]+?\"?([^\.\,\"]+)\"?,/", $j, $matches );

            if ( isset($matches[3]) && $matches[3] != '' ) $array[$matches[3]] = $matches[3];
        }
        //var_dump($array);
    
        return new TObject( $array );
    }

    protected function pictures( $node, $url='' )
    {
        $tds = $node->getElementsByTagName( 'td' );
        foreach ( $tds as $td )
        {
            if ( ($attr = $td->attributes->getNamedItem( 'class' )) !== null )
            {
                if ( $attr->value == 'singleProdV-tdSmallPhoto' )
                {
                    if ( ($img = $this->getElement( $td, 'img' )) !== null )
                    {
                        $click = $img->attributes->getNamedItem( 'onclick' )->value;
                        $c = explode( ",", $click );
                        $array[] = str_replace( "'", "", trim($c[1]) );
                    }
                }
            }
        }
        
        $td = $this->getElement( $node, 'td', 'singleProdV-tdProdLeft' );
        if ( ($elements = $td->getElementsByTagName( 'script' )) !== null )
        {
            
            $expjs = explode( ';', $elements->item(1)->nodeValue );
            foreach( $expjs as $j )
            {
                preg_match( "/new CProductAdditional\(([^\.\,\s]+)\,[\s]+?\'([^\,\s]+)\'\,[\s]+?\'([^\,\s]+)\'\,[\s]+?\'([^\,\s]+)\'/", $j, $matches );

                if ( isset($matches[3]) && $matches[3] != '' ) $array[] = $matches[4].$matches[3];
            }
        }
        return $array;
    }

    protected function description( $node )
    {
        return trim( $this->getElementValue( $node, 'span', 'description' ) );
    }

    protected function articul( $node )
    {  
        return null;
    }
    protected function articulByUrl($url)
    {
        $urlp = parse_url($url);
        foreach (explode('&', $urlp->query) as $chunk) {
            $param = explode("=", $chunk);

            if ($param[0]=="productID") {
                return $param[1];
            }
        }
    }
}

?>