

tinymce.PluginManager.add('insert_image', function(editor, url) {
    
    // Add a button that opens a window
    editor.addButton('insert_image', {
        text: 'Загрузить',
        icon: 'image',
        onclick: function() {
            // Open window with a specific url
            editor.windowManager.open({
                title: 'Загрузить Изображение',
                url: '/ajax/insert_image?dialog=upload',
                width: 400,
                height: 100,
                buttons: [{
                    text: 'Закрыть',
                    onclick: 'close'
                }]
            });
        }
    });

    // Adds a menu item to the tools menu
    editor.addMenuItem('insert_image', {
        text: 'Загрузить',
        context: 'insert',
	icon : 'image',
        onclick: function() {
            // Open window with a specific url
            editor.windowManager.open({
                title: 'Загрузить Изображение',
                url: '/ajax/insert_image?dialog=upload',
                width: 400,
                height: 100,
                buttons: [{
                    text: 'Закрыть',
                    onclick: 'close'
                }]
            });
        }
    });
});